    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        nAdultsBox.setEnabled(false);
        nChildrenBox.setEnabled(false);
        nSeatsBox.setEnabled(false);
        jComboBox5.setEnabled(false);
        jComboBox2.setEnabled(false);
        jComboBox6.setEnabled(false);
        jComboBox7.setEnabled(false);
        
        jButton1.setEnabled(false);
        jButton2.setEnabled(true);
        jButton3.setEnabled(false);
        jButton4.setEnabled(true);
        
        // Read values
        int nAdults = Integer.valueOf(nAdultsBox.getText());
        int nChildren = Integer.valueOf(nChildrenBox.getText());
        int nSeats = Integer.parseInt( (String) nSeatsBox.getSelectedItem() );
       
        String timeToMove = String.valueOf(jComboBox5.getSelectedItem());
        
        Random r = new Random();
        
        int low = 0;
        int high = 0;
        
        int ttm = 2;
        switch(timeToMove){
            case "0":
                ttm = 0; break;
            case "100":
                ttm = 100; break;
            case "random (0;100)":
                high = 100;
                ttm = r.nextInt(high-low) + low; break;
            case "random(0;250)":
                high = 250;
                ttm = r.nextInt(high-low) + low; break;
            case "random(0;500)":
                high = 500;
                ttm = r.nextInt(high-low) + low; break;
            case "random(0;1000)":
                high = 1000;
                ttm = r.nextInt(high-low) + low; break;
        }
        
        String evaluationTime = String.valueOf(jComboBox2.getSelectedItem());
        
        String medicalTime = String.valueOf(jComboBox6.getSelectedItem());
        
        String paymentTime = String.valueOf(jComboBox7.getSelectedItem());

        String mode;
        if (jRadioButton1.isSelected() ){
            mode = "manual";
        } else {
            mode = "auto";
        }
        
        try {
            CCP_Main.afterStart(nAdults, nChildren, nSeats, ttm, evaluationTime, medicalTime, paymentTime);
        } catch (IOException ex) {}
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

