    private void nAdultsBoxKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_nAdultsBoxKeyTyped
        // TODO add your handling code here:
        char c = evt.getKeyChar();

        if (!Character.isDigit(c)){
            evt.consume();
        }
        else if (nAdultsBox.getText().length() > 1){
            evt.consume();
        }
        else if (nAdultsBox.getText().equals("6") 
                || nAdultsBox.getText().equals("7") 
                || nAdultsBox.getText().equals("8") 
                || nAdultsBox.getText().equals("9") ){
            evt.consume();
        }
        else if (nAdultsBox.getText().equals("5")){
            if (c!='0'){
                evt.consume();
            }
        }
    }//GEN-LAST:event_nAdultsBoxKeyTyped

